def like_bonjour_monde(string, nb)
    client = login_twitter
    client.search(string).take(nb).each do |tweet|
    client.fav(tweet)
    end
end 
def follow_bonjour_monde(string, nb) #suis les users qui ont posté un message en scannant les nb derniers messages
    client = login_twitter
    client.search(string).take(nb).each do |tweet|
    client.follow(tweet.user)
    end
    #nb c'est le nombre de compte qu'on va suivre
end 

def follow_hashtag (string, nb) #même chose que la précédente mais va attendre d'avoir un nombre nb de users avant de mettre fin à la boucle
    client = login_twitter
    followed_users = 0
    a=1
    list=[]
    until followed_users == nb #boucle jusqu'à atteindre un nombre de users
        client.search(string).take(a).each do |tweet|
            if tweet.user != client.user #pour ne pas m'ajouter dans la liste
                list << tweet.user
            end
        end
        a += 1
        list = list.uniq
        followed_users = list.size
    end
    #puts list
    for i in 0..nb-1 #pour chaque user du tableau
        client.follow(list[i]) #follow les users
    end
end

def login_twitter_stream #pour streamer Site Streams are restricted to whitelisted accounts. 
client = Twitter::Streaming::Client.new do |config|
    config.consumer_key        = ENV["TWITTER_CONSUMER_KEY"]
    config.consumer_secret     = ENV["TWITTER_CONSUMER_SECRET"]
    config.access_token        = ENV["TWITTER_ACCESS_TOKEN"]
    config.access_token_secret = ENV["TWITTER_ACCESS_TOKEN_SECRET"]
  end
end

def stream_twitt (string)
    client_stream = login_twitter_stream #les accès stream sont différents de ceux Rest
    client = login_twitter
    client_stream.filter(track:string) do |object|
        if object.is_a?(Twitter::Tweet)
            client.fav(object)
            client.follow(object.user)
            print object.user #pour voir qui a tweeter
            puts object.text #pour voir le tweet
        end
    end
end

